import logo from '../images/header-logo.svg';
import { Link, useLocation } from 'react-router-dom';

function Header() {
  const location = useLocation();
  const url = location.pathname;

  const buttonLink = () => {
    if (url === '/sign-in') {
      return '/sign-up'
    } else
      if (url === '/sign-up') {
        return '/sign-in'
      } else
        if (url === '/') {
          return '/sign-in'
        }
  }

  const buttonText = () => {
    if (url === '/sign-in') {
      return 'Регистрация'
    } else
      if (url === '/sign-up') {
        return 'Войти'
      } else
        if (url === '/') {
          return 'Выйти'
        }
  }

  return (
    <header className='header'>
      <img className='header__logo' src={logo} alt='Назание сайта: Mesto Russia' />
      <div className='header__auth'>
        <p className='header__email'>{props.userEmailInfo}</p>
        <Link
          className={(navData) => navData.isActive ? 'header__button_hidden' : 'header__button'}
          to={buttonLink()}>{buttonText()}</Link>
        <button
          onClick={props.logout}
          className={(navData) => navData.isActive ? 'header__button' : 'header__button_hidden'}>Выход</button>
      </div>

    </header>
  )
}

export default Header
