import logo from '../images/header-logo.svg';
import { Link } from 'react-router-dom';

function Header(props) {
  const url = props.url;

  const buttonLink = () => {
    if (url === '/sign-in') {
      return '/sign-up'
    } else
      if (url === '/sign-up') {
        return '/sign-in'
      } 
  }

  const buttonText = () => {
    if (url === '/sign-in') {
      return 'Регистрация'
    } else
      if (url === '/sign-up') {
        return 'Войти'
      }
  }

  return (
    <header className='header'>
      <img className='header__logo' src={logo} alt='Назание сайта: Mesto Russia' />
      <div className='header__auth'>
        <p className={props.isloggedIn ? 'header__email' : ''}>{props.userEmailInfo}</p>
        <Link
          className='header__button'
          to='/sign-up'></Link>
          <Link
          className='header__button'
          to={buttonLink()}>{buttonText()}</Link>
        <button
          onClick={props.logout}
          className={props.isloggedIn ? 'header__logout' : ''}>Выход</button>
      </div>

    </header>
  )
}

export default Header
