import logo from '../images/header-logo.svg';
import { Link, useLocation } from 'react-router-dom';

function Header() {
  const location = useLocation();
  const url = location.pathname;
  console.log(url === '');

  const buttonLink = () => {
    if (url === '/sign-in') {
      return '/sign-up'
    } else
      if (url === '/sign-up') {
        return '/sign-in'
      } else
        if (url === '/') {
          return '/sign-in'
        }
  }

  const buttonText = () => {
    if (url === '/sign-in') {
      return 'Регистрация'
    } else
      if (url === '/sign-up') {
        return 'Войти'
      } else
        if (url === '/') {
          return 'Выйти'
        }
  }

  return (
    <header className='header'>
      <img className='header__logo' src={logo} alt='Назание сайта: Mesto Russia' />
      <Link className='header__button' to={buttonLink}>{buttonText}</Link>
    </header>
  )
}

export default Header
